CREATE PROCEDURE [dbo].[web_buddybuddy_transfer]
(
	@accountIDX int
,	@userPWD varchar(24)
,	@mailling int
,	@userName varchar(10)
,	@userbirthday char(6)
,	@userEnCode varchar(64)
,	@userNumber tinyint
,	@userGender tinyint
)
AS
BEGIN

	SET NOCOUNT ON

	DECLARE @channelType int

	SELECT @channelType = cpType
	FROM userMemberDB WITH(NOLOCK)
	WHERE accountIDX=@accountIDX

	IF (@@ROWCOUNT = 0)
	BEGIN
		RETURN (-1)
	END

	IF (@channelType <> 500)
  	BEGIN
		RETURN (-2)
	END

	IF (EXISTS (SELECT * FROM userLoginDB WITH(NOLOCK) WHERE accountIDX=@accountIDX AND gameServerID > 0))
  	BEGIN
		RETURN (-3)
	END

	IF (EXISTS (SELECT * FROM userNameDB WITH(NOLOCK) WHERE userEnCode=@userEnCode))
  	BEGIN
		RETURN (-4)
	END


	BEGIN TRAN

	UPDATE userNameDB
	SET userName = @userName
	,	userbirthday = @userbirthday
	,	userEnCode = @userEnCode
	,	userNumber = @userNumber
	,	userGender = @userGender
	,	returnValue = 0
	WHERE accountIDX = @accountIDX

	IF (@@ROWCOUNT <> 1 OR @@ERROR <> 0)
	BEGIN
		ROLLBACK TRAN
		RETURN (-5)
	END

	UPDATE userMemberDB
	SET userPWD = @userPWD
	,	joinType = 5
	,	cpType = 0
	,	mailling = @mailling
	WHERE accountIDX = @accountIDX

	IF (@@ROWCOUNT <> 1 OR @@ERROR <> 0)
	BEGIN
		ROLLBACK TRAN
		RETURN (-6)
	END

	DELETE FROM userCooperationDB WHERE accountIDX=@accountIDX AND cpType=500

	IF (@@ROWCOUNT <> 1 OR @@ERROR <> 0)
	BEGIN
		ROLLBACK TRAN
		RETURN (-7)
	END

	COMMIT TRAN
	RETURN (1)  

END
GO


