CREATE PROCEDURE [dbo].[game_matchmode_set_data]              
              
 @ACCOUNT_IDX   INT,      
 @PLAY_COUNT INT,              
 @MATCH_POINT   INT,              
 @WIN_COUNT    INT,              
 @MAX_WIN_COUNT INT,    
 @LOSE_COUNT INT,  
 @RANK_MMR   INT  
               
              
AS              
              
 SET NOCOUNT ON;              
