	@intAccountIdx		int,
	@strLoginID			varchar(25),
	@strMethod			char(1),
	@strLocation		varchar(20),
	@strRequestNo		varchar(30),
	@strPlainData		varchar(400),
	@strRegIP			varchar(15)
AS
/*
	@strMethod : M : 핸드폰인증, X : 공인인증서, C : 신용카드, B : 은행, I : 아이핀

	Grant Execute on dbo.USP_LTS_AuthCheck_INS to usr_websvc
*/
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

    -- Insert statements for procedure here

	IF Len(@strRequestNo) > 5
		Begin
			IF Not Exists (Select 1 From dbo.TB_LTS_AuthCheck_Log with (noLock)
							Where Acl_RequestNo = @strRequestNo
			)
				Begin

					Begin Tran
					INSERT INTO [dbo].[TB_LTS_AuthCheck_Log] (
						[Acl_AccountIdx]
						, [Acl_LoginID]
						, [Acl_Method]
						, [Acl_Location]
						, [Acl_RequestNo]
						, [Acl_PlainData]
						, [Acl_RegIP]
						, [Acl_RegDate]
					) VALUES (
						@intAccountIdx
						, @strLoginID
						, @strMethod
						, @strLocation
						, @strRequestNo
						, @strPlainData
						, @strRegIP
						, getdate()
					)

					IF @@ERROR = 0
						Begin
							Commit Tran
							Return 1			-- 정상 처리
						End
					Else
						Begin
							RollBack Tran
							Return -9			-- 처리 중 오류
						End
				End
			Else
				Begin
					Return -7					-- 이미 등록됨
				End

		End
	Else
		Begin
			Return -8			-- 잘 못된 요청(파라미터 오류)
		End


END
GO


