-- ----------------------------
IF EXISTS (SELECT * FROM sys.all_objects WHERE object_id = OBJECT_ID(N'[dbo].[USP_LTS_Channel_LegalAgent_CHILD_LIST]') AND type IN ('P', 'PC', 'RF', 'X'))
