		,	type3_lose	= @type3_lose
		,	type3_kill	= @type3_kill
		,	type3_death	= @type3_death
		,	type4_win	= @type4_win
		,	type4_lose	= @type4_lose
		,	type4_kill	= @type4_kill
		,	type4_death	= @type4_death
		WHERE accountIDX = @accountIDX

	END
GO


