CREATE PROCEDURE [dbo].[game_game_save]
(
	@accountIDX int
,	@userState int
,	@gameMoney bigint
,	@conn_count int
,	@connDate datetime
,	@userLevel int
,	@userEXP int
,	@playTime int
,	@regionType int
,	@userFishingLevel int
,	@userFishingEXP int
,	@refillData int
,	@userExcavationLevel int
,	@userExcavationEXP int
,	@init_time int
)
As

SET NOCOUNT ON

IF @userLevel < 50
BEGIN
	-- 기존 루틴 그대로
	UPDATE dbo.userGameDB
	   SET gameMoney = @gameMoney
	,	userState = @userState
	,	playTime = playTime + Round((@playTime/1000)/60,0)
	,	conn_count = @conn_count
	,	userLevel = @userLevel
	,	userEXP = @userEXP
	,	userFishingLevel = @userFishingLevel
	,	userFishingEXP = @userFishingEXP
	,	regionType = @regionType
	,	connDate = @connDate
	,	refillData = @refillData
	,	userExcavationLevel = @userExcavationLevel
	,	userExcavationEXP = @userExcavationEXP 
	, Practice = 0
	 WHERE accountIDX = @accountIDX

END
ELSE
BEGIN
	--
	IF( EXISTS( SELECT * FROM dbo.userRelativeLevelDB WITH( NOLOCK )
				 WHERE accountIDX = @accountIDX ) )
	BEGIN
		
		UPDATE dbo.userGameDB
		   SET gameMoney = @gameMoney
		,	userState = @userState
		,	playTime = playTime + Round((@playTime/1000)/60,0)
		,	conn_count = @conn_count
		--,	userLevel = @userLevel
		,	userEXP = @userEXP
		,	userFishingLevel = @userFishingLevel
		,	userFishingEXP = @userFishingEXP
		,	regionType = @regionType
		,	connDate = @connDate
		,	refillData = @refillData
		,	userExcavationLevel = @userExcavationLevel
		,	userExcavationEXP = @userExcavationEXP 
		, Practice = 0
		 WHERE accountIDX = @accountIDX

		UPDATE dbo.userRelativeLevelDB
		   SET init_time = @init_time
		 WHERE accountIDX = @accountIDX

	END
	ELSE
	BEGIN
		-- 기존 루틴 그대로 : 혹시나 ?
		UPDATE dbo.userGameDB
		   SET gameMoney = @gameMoney
		,	userState = @userState
		,	playTime = playTime + Round((@playTime/1000)/60,0)
		,	conn_count = @conn_count
		,	userLevel = @userLevel
		,	userEXP = @userEXP
		,	userFishingLevel = @userFishingLevel
		,	userFishingEXP = @userFishingEXP
		,	regionType = @regionType
		,	connDate = @connDate
		,	refillData = @refillData
		,	userExcavationLevel = @userExcavationLevel
		,	userExcavationEXP = @userExcavationEXP 
		, Practice = 0
		 WHERE accountIDX = @accountIDX
	END
END
GO


