CREATE PROCEDURE [dbo].[USP_LTS_Coupon_Use_SEL] 
	-- Add the parameters for the stored procedure here
	@intType			tinyint,
	@intcpType			smallint,
	@intAccountIDX		int,
	@strLoginID			varchar(50),
	@intMaxNum			tinyint,
	@strRegIP			varchar(15)
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

    -- Insert statements for procedure here
	Declare @intUseCnt		tinyint

	-- 쿠폰 사용 수를 구하다.
	Set @intUseCnt = (Select count(*) From TB_EventCoupon_Use with (noLock)
					Where CPN_Type = @intType
					And CPN_AccountIDX = @intAccountIDX
					And CPN_UserID = @strLoginID
					And CPN_DelFlag = 0)

	-- 쿠폰 사용 횟수 비교
	IF @intMaxNum > @intUseCnt
		Begin
			Begin Tran

			Insert Into TB_EventCoupon_Use (
				CPN_Type, 
				CPN_cpType,
				CPN_AccountIDX,
				CPN_UserID,
				CPN_RegIP,
				CPN_RegDate
			) Values (
				@intType,
				@intcpType,
				@intAccountIDX,
				@strLoginID,
				@strRegIP,
				getdate()
			)

			IF @@ERROR = 0
				Begin
					Commit Tran
					Return 1			-- 사용 성공
				End
			Else
				Begin
					RollBack Tran
					Return 9			-- 사용 실패
				End
		End
	Else
		Begin
			Return 0					-- 이미 사용한 쿠폰
		End


END
GO


