CREATE PROCEDURE [dbo].[game_oakbarrel_data_update]              
              
 @ACCOUNT_IDX INT,              
 @STEP			TINYINT,              
 @Slot1		 TINYINT,               
 @Slot2      TINYINT,               
 @Slot3      TINYINT,               
 @Slot4      TINYINT,               
 @Slot5      TINYINT,               
 @Slot6      TINYINT,               
 @Slot7      TINYINT,               
 @Slot8      TINYINT,               
 @Slot9      TINYINT,               
 @Slot10      TINYINT,               
 @Slot11      TINYINT,               
 @Slot12      TINYINT,  
 @LimitSword    INT             
AS                
              
 SET NOCOUNT ON;              
 SET LOCK_TIMEOUT 10000;              
               
 BEGIN TRAN              
  UPDATE dbo.userOakBarrelDB WITH (XLOCK, ROWLOCK)               
     SET step=@Step,               
      Slot1=@Slot1,               
      Slot2=@Slot2,               
      Slot3=@Slot3,               
      Slot4=@Slot4,               
      Slot5=@Slot5,               
      Slot6=@Slot6,               
      Slot7=@Slot7,               
      Slot8=@Slot8,               
      Slot9=@Slot9,              
      Slot10=@Slot10,               
      Slot11=@Slot11,               
      Slot12=@Slot12,  
      LimitSword=@LimitSword               
   WHERE AccountIDX = @ACCOUNT_IDX;              
          
   IF(@@ROWCOUNT=0)              
   BEGIN              
    INSERT dbo.userOakBarrelDB WITH(XLOCK, ROWLOCK) ( AccountIDX, Step, Slot1, Slot2, Slot3, Slot4, Slot5, Slot6, Slot7, Slot8, Slot9, Slot10, Slot11, Slot12, LimitSword, updateTime, regDate )                
